DATA SEGMENT:
MSG1 DB 10, 13, "ENETR A  STRING $"
MSG2 DB 10, 13, "PALINDROME $"
MSG3 DB 10, 13, "NOT PALINDROME $"
DATA ENDS

CODE SEGMENT:
ASSUME CS:CODE, DS:DATA
START:
MOV AX,DATA
MOV DS,AX
LEA DX,MSG1
MOV AH,09H
INT 21H
LEA SI,STR1
LEA DI,STR1
MOV AH,01H
NEXT:
INT 21H
CMP AL,0DH
JNE TERM
MOV [DI],AL
INC DI
JMP NEXT
TERM:
MOV AL,'$;
MOV DI,AL
DOTHIS:
DEC DI
MOV AL,[SI]
CMP SI,DI
JNE NOTPALINDROME
INC SI
CMP SI,DI
JL DOTHIS
PALINDROME:
MOV AH,09H
LEA DX,MSG2
INT 21H
JMP XX
NOTPALINDROME:
MOV AH,09H
LEA DX,MSG3
JMP XX
XX:
MOV AH,4CH
INT 21H
CODE ENDS
END START
